<!DOCTYPE html>
<html>
<head>
    <title>Shadow Daemon - <?php echo htmlspecialchars($this->getTitle()); ?></title>
    <style>
        .content {
            font-family: Tahoma, "Helvetica Neue", Helvetica, Arial, sans-serif;
            font-size: 14px;
            margin: auto;
            width: 50%;
            height: 50%;
            position: absolute;
            top: 0;
            left: 0;
            bottom: 0;
            right: 0;
        }

        .info {
            background-color: rgb(217, 237, 247);
            border-style: solid;
            border-width: 1px;
            border-color: rgb(188, 232, 241);
            border-radius: 4px;
            box-sizing: border-box;
            color: rgb(58, 135, 173);
            padding: 50px;
            width: 800px;
        }

        .info h1 {
            margin-top: 0;
        }

        .debug {
            border-top: 1px solid;
            overflow: auto;
        }

        .debug .warning {
            font-size: 10px;
        }

        a {
            text-decoration: none;
            color: rgb(58, 90, 173);
        }
    </style>
</head>
<body>
    <div class="content">
        <div class="info">
            <img alt="Shadow Daemon" src="data:image/png;base64," />
            <div class="description">
                <h1><?php echo htmlspecialchars($this->getTitle()); ?></h1>
                <?php $this->printDescription(); ?>
            </div>

            <?php if ($this->isDebug() && $this->isException()): ?>
                <div class="debug">
                    <h2><?php echo htmlspecialchars($this->getExceptionClass()); ?></h2>
                    <p><?php echo htmlspecialchars($this->getExceptionMessage()); ?></p>
                    <pre class="stacktrace"><?php echo htmlspecialchars($this->getStackTrace()); ?></pre>
                    <span class="warning">You are seeing this stack trace because the debug mode is enabled. Debug output might leak information and should be disabled when not needed.</span>
                </div>
            <?php endif; ?>
        </div>
    </div>
    </body>
</html>